import React, { useState } from 'react';
import styled from 'styled-components';

const MessageContainer = styled.div`
  background-color: ${props => props.isUser ? '#E3F2FD' : '#F5F5F5'};
  padding: 10px;
  border-radius: 5px;
  margin: 5px 0;
`;

const MessageInput = styled.input`
  width: 80%;
  padding: 10px;
  margin-right: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const SendButton = styled.button`
  padding: 10px 20px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const MessageList = styled.div`
  height: 300px;
  overflow-y: auto;
  border: 1px solid #ccc;
  padding: 10px;
  margin-bottom: 10px;
`;

function Convo() {
  const [userMessages, setUserMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');

  const handleInputChange = (e) => {
    setInputMessage(e.target.value);
  };

  const handleSubmit = () => {
    if (inputMessage.trim() !== '') {
      setUserMessages([...userMessages, inputMessage]);
      setInputMessage('');
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const printMessage = (message, isUser) => {
    return (
      <MessageContainer isUser={isUser}>
        <p>{message}</p>
      </MessageContainer>
    );
  };

  return (
    <div style={{marginBottom: "100px"}}>
      <MessageList>
        {userMessages.map((message, index) => printMessage(message, true))}
      </MessageList>
      <div>
        <MessageInput 
          type="text" 
          value={inputMessage} 
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
        />
        <SendButton onClick={handleSubmit}>Send</SendButton>
      </div>
    </div>
  );
}

export default Convo;
