import React, { useState, useEffect } from 'react';
import styled from 'styled-components';

const MessageContainer = styled.div`
  background-color: ${props => props.isUser ? '#E3F2FD' : '#F5F5F5'};
  padding: 10px;
  border-radius: 5px;
  margin: 5px 0;
`;

const MessageInput = styled.input`
  width: 80%;
  padding: 10px;
  margin-right: 10px;
  border: 1px solid #ccc;
  border-radius: 5px;
`;

const SendButton = styled.button`
  padding: 10px 20px;
  background-color: #4CAF50;
  color: white;
  border: none;
  border-radius: 5px;
  cursor: pointer;

  &:hover {
    background-color: #45a049;
  }
`;

const MessageList = styled.div`
  height: 300px;
  overflow-y: auto;
  border: 1px solid #ccc;
  padding: 10px;
  margin-bottom: 10px;
`;

function Convo() {
  const [messages, setMessages] = useState([]);
  const [inputMessage, setInputMessage] = useState('');

  const handleInputChange = (e) => {
    setInputMessage(e.target.value);
  };

  const handleSubmit = () => {
    if (inputMessage.trim() !== '') {
      const newUserMessage = { text: inputMessage, isUser: true };
      setMessages(prevMessages => [...prevMessages, newUserMessage]);
      setInputMessage('');

      // Simulate API call with 5-second delay
      setTimeout(() => {
        const apiResponse = { text: "This is a placeholder response from the API.", isUser: false };
        setMessages(prevMessages => [...prevMessages, apiResponse]);
      }, 2000);
    }
  };

  const handleKeyPress = (e) => {
    if (e.key === 'Enter') {
      handleSubmit();
    }
  };

  const printMessage = (message) => {
    return (
      <MessageContainer isUser={message.isUser}>
        <p>{message.text}</p>
      </MessageContainer>
    );
  };

  useEffect(() => {
    // Scroll to bottom of message list when new messages are added
    const messageList = document.querySelector('.message-list');
    if (messageList) {
      messageList.scrollTop = messageList.scrollHeight;
    }
  }, [messages]);

  return (
    <div style={{marginBottom: "100px"}}>
      <MessageList className="message-list">
        {messages.map((message, index) => (
          <div key={index}>{printMessage(message)}</div>
        ))}
      </MessageList>
      <div>
        <MessageInput 
          type="text" 
          value={inputMessage} 
          onChange={handleInputChange}
          onKeyPress={handleKeyPress}
          placeholder="Type your message..."
        />
        <SendButton onClick={handleSubmit}>Send</SendButton>
      </div>
    </div>
  );
}

export default Convo;
